let data_array = [];
let data = fetch("https://jsonplaceholder.typicode.com/users");
let baap = document.getElementById("baap");
data
    .then((response)=>{
        // console.log(response.json());
        return response.json();
    })
    .then((value)=>{
        // console.log(value);
        data_array = value;
        fall(data_array);
        
    })
    function fall(value){
       let number = localStorage.getItem("num");
       let id = number*1;
       id -= 1;
        baap.innerHTML = card(value[id]);
       
    }

    function card(obj){
        return`
             
            <div class="side_img">
                <a href="promise_localstg.html">
                    <img src="https://cdn-icons-png.flaticon.com/512/109/109618.png" alt="">
                </a>
                <p>Previous page</p>
            </div>

            <div class="card">

                <div class="card_company">
                    <h1>${obj.company.name}</h1> 
                </div>

                    <div class="card_details">
                        <div class="card_img">
                            <img src="data:image/png;base64," alt="">
                        </div>
            
                        <div class="card_name">
                            <h1>${obj.name}</h1>
                        </div>
                    </div>

                    <div class="card_data">
                        <div class="card_data_id">
                            <h3>ID : ${obj.id}</h3>
                        </div>
                        <div class="card_data_username">
                            <h3>Username : ${obj.username}</h3>
                        </div>
                        <div class="card_data_phone">
                            <h3>Phone : ${obj.phone}</h3>
                        </div>
                        <div class="card_data_email">
                            <h3>Email : ${obj.email}</h3>
                        </div>
                    </div>
                    
                    <div class="footer">

                    </div>

            </div>

            <div class="side_img">
                <a href="promise_extra.html">
                    <img src="https://banner2.cleanpng.com/20180426/hpq/ave7blxhb.webp" alt="">
                </a>
                <p>Extra data</p>
            </div>

             `
    }